mapper.select:
        select count(*)
		  from t_repair_order t
		  left join t_category t1 on t.first_category_id = t1.id
		  left join t_category t2 on t.second_category_id = t2.id
		  left join t_shop t3 on t.shop_id = t3.id
		  left join t_user t4 on t.create_user_id = t4.id
		  left join t_brand t5 on t.brand_id = t5.id
		  left join t_customer t6 on t.customer_id = t6.id
		  left join t_consult_order t7 on t.consult_order_id = t7.id
		  left join t_user t8 on t7.create_user_id = t8.id
		 where 1 = 1
        and t.create_at >= #{startDate}
        and t.create_at <= #{endDate}
        and t.shop_id = #{shopId}
        and t1.name = #{firstCategory}
        and t2.name = #{secondCategory}
        and t7.create_user_id = #{onLineUser}
        and t.create_user_id = #{shopUser}
        and t7.deliver_type = #{deliverType}
        and t6.type = #{customerType}
        and t7.channel_source = #{channelSource}
        and t.status in 

                #{item}

select t3.name shop_name, t.create_at, t.order_no, t.pickup_at, t8.realname consult_create_user_name, t4.realname create_user_name, t7.deliver_type, t.status, t5.name brand_name, t1.name first_category_name, t2.name second_category_name, t.service_item_ids, t.repair_desc, t.total_payment, t.material_payment, t.discount_amount_payment, t.advance_payment, t.customer_name, case t6.sex when '0' then '女' when '1' then '男' end customer_sex, t.customer_phone, t6.province, t6.city, t.customer_address, t6.wechat_nickname, t6.wechat_id, t6.qq customer_qq, case t6.type when '0' then '新客户' when '1' then '老客户' end customer_type, t6.channel_source, t7.create_at consult_create_at from t_repair_order t left join t_category t1 on t.first_category_id = t1.id left join t_category t2 on t.second_category_id = t2.id left join t_shop t3 on t.shop_id = t3.id left join t_user t4 on t.create_user_id = t4.id left join t_brand t5 on t.brand_id = t5.id left join t_customer t6 on t.customer_id = t6.id left join t_consult_order t7 on t.consult_order_id = t7.id left join t_user t8 on t7.create_user_id = t8.id where 1 = 1 and t.create_at >= #{startDate} and t.create_at <= #{endDate} and t.shop_id = #{shopId} and t1.name = #{firstCategory} and t2.name = #{secondCategory} and t7.create_user_id = #{onLineUser} and t.create_user_id = #{shopUser} and t7.deliver_type = #{deliverType} and t6.type = #{customerType} and t7.channel_source = #{channelSource} and t.status in
#{item}
order by t.id desc limit #{pageHelper.offset},#{pageHelper.size}
